CREATE TABLE IF NOT EXISTS fe_exames (ex_id INT NOT NULL AUTO_INCREMENT, ex_descricao VARCHAR(500), ex_minemonico VARCHAR(50), ex_unidade VARCHAR(50), ex_prazo VARCHAR(50), ex_valor VARCHAR(50), ex_status BOOLEAN DEFAULT FALSE, ex_cancelado BOOLEAN DEFAULT FALSE, ex_data_abertura TIMESTAMP DEFAULT '0000-00-00 00:00:00', ex_data_fechamento TIMESTAMP DEFAULT '0000-00-00 00:00:00', ex_paciente_os VARCHAR(50), ex_observacao VARCHAR(1000), fe_material INT, fe_acoes INT, ws_users INT, ws_users_soli INT, ws_setor_soli INT, ws_setor_exec INT, PRIMARY KEY (ex_id));
CREATE TABLE IF NOT EXISTS fe_material(mat_id INT NOT NULL AUTO_INCREMENT, mat_descricao  VARCHAR(50), mat_status BOOLEAN, PRIMARY KEY (mat_id ));
CREATE TABLE IF NOT EXISTS fe_acoes(acao_id INT NOT NULL AUTO_INCREMENT, acao_descricao  VARCHAR(50), acao_status BOOLEAN, PRIMARY KEY (acao_id ));